#' @export
pkPal <- pal




# convert cpt files to  a color vector
cpt2hexvec <- function(fn, rev=F){
  require(stringr)
  s   <- readLines(fn)
  s   <- s[!grepl('#',s,)]
  s   <- s[!grepl('^B',s)]
  s   <- s[!grepl('^F',s)]
  s   <- s[!grepl('^N',s)]
  s   <- gsub("\\s+", " ",s)
  s   <- str_split(s, ' ')
  if(rev){s <- rev(s)}
  hex <- sapply(s, function(x) rgb(as.numeric(x[2])/255,as.numeric(x[3])/255,as.numeric(x[4])/255))
  cat(paste0('c(',paste0('"',hex,collapse='",'),'"),'))
}
